CREATE TABLE IF NOT EXISTS EVENTS_TABLE(id INTEGER PRIMARY KEY AUTOINCREMENT,Event_ID INTEGER , EVENT_NAME TEXT);
CREATE TABLE IF NOT EXISTS EVENTS_TABLE_NEW(id INTEGER PRIMARY KEY AUTOINCREMENT,Event_ID INTEGER CONSTRAINT Event_ID_UNIQUE UNIQUE , EVENT_NAME TEXT,IS_COPARTS TEXT);
CREATE TABLE IF NOT EXISTS PARTICIPANTS_TABLE(id INTEGER PRIMARY KEY AUTOINCREMENT,Event_ID INTEGER , PARTICIPANTS_ID INTEGER,MEMBER_ID INTEGER,PARTICIPANTS_NAME TEXT,PARTICIPANTS_TYPE TEXT,PARTICIPANTS_ID_CONSTRAINT INTEGER CONSTRAINT PARTICIPANTS_ID_CONSTRAINT_UNIQUE UNIQUE );
CREATE TABLE IF NOT EXISTS QUESTION_TABLE_REAL(id INTEGER PRIMARY KEY AUTOINCREMENT,Event_ID INTEGER , PARTICIPANTS_ID INTEGER ,QUESTION_ID INTEGER ,QUESTIONS TEXT ,OPTIONS TEXT ,QUESTION_TYPE TEXT , QUESTION_ID_CONSTRAINT INTEGER CONSTRAINT QUESTION_ID_CONSTRAINT_UNIQUE UNIQUE);
CREATE TABLE IF NOT EXISTS QUESTION_TABLE(id INTEGER PRIMARY KEY AUTOINCREMENT,Event_ID INTEGER , PARTICIPANTS_ID INTEGER ,QUESTION_ID INTEGER ,QUESTIONS TEXT ,OPTIONS TEXT);
CREATE TABLE IF NOT EXISTS RESPONSE_TABLE(id INTEGER PRIMARY KEY AUTOINCREMENT,Event_ID INTEGER , PARTICIPANTS_ID INTEGER ,QUESTION_ID INTEGER ,RESPONSE TEXT);
